
module.exports = function getHtml(list) {
    let iconList=[
        {exname:".js",src:"js_icon.jpg"},
        {exname:".txt",src:"file.jpg"},
        {exname:"dir",src:"dir.jpg"},
        {exname:".html",src:"html_icon.jpg"},
        {exname:".jpg",src:"img_icon.jpg"},
        {exname:".json",src:"json_icon.jpg"}
    ];
    return `
        ${(function (list) {
            return list.reduce((value,item) => {
                let extname = iconList.filter(icon => {
                    return icon.exname===item.type;
                })[0].src;
                return value + `
                    <li><a href="${item.path}"><img src="http://10.9.47.242:2000/fsmyserve/img/${extname}">${item.name}</a></li>
                `
            },"")
        })(list)}
    `;
}